import React from 'react';

function TransactionHistory({ transactions, onDeleteTransaction,filter }) {
    let new_list = transactions;
        // console.log(flist);
        // console.log(new_list)
        new_list = new_list.filter(elem => {
            if (filter.name === "") return true;
            return elem.name === filter.name
        });
        new_list = new_list.filter(elem => {
            if (filter.payment === "") return true;
            return elem.payment === filter.payment
        });
        new_list = new_list.filter(elem => {
            if (filter.date === "") return true;
            return elem.date === filter.date
        });
        new_list = new_list.filter(elem => {
            if (filter.amount === "") return true;
            return elem.amount === filter.amount
        });

    return (
        <div>
            <h2>Transaction History</h2>
            <p className='Transaction_list_heading'>Type Name Date Category Amount</p>
            <ul className='transactions'>
                {
                    new_list.map((data) => 
                        <li key={data.id}>
                            <div>{data.billtype}</div>
                            <div>{data.name}</div>
                            <div>{data.date}</div>
                            <div>{data.category}</div>
                            <div>
                                <span>₹ {data.amount}</span>
                                <button onClick={() => onDeleteTransaction(data.id)}>␡</button>
                            </div>

                        </li>
                    )
                }
            </ul>
        </div>
    )
}

export default TransactionHistory;
